import { useEffect, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../css/CustomerDashboard.css";
import { isAuthenticated } from "../utils/auth";
import {
  getUserName,
  getUserAccountNum,
  getPayments,
  getBalance,
} from "../services/dataRequestService";

function CustomerDashboard() {
  const navigate = useNavigate();
  const alertShown = useRef(false); // Ref to track if the alert has been shown
  const [authChecked, setAuthChecked] = useState(false); // State to track if auth check is done

  useEffect(() => {
    if (!isAuthenticated() && !alertShown.current) {
      alert("You are not logged in. Please log in to continue.");
      alertShown.current = true; // Set the ref to true after showing the alert
      navigate("/login");
    } else {
      setAuthChecked(true); // Set auth check to true if authenticated
    }
  }, [navigate]);

  const [username, setUsername] = useState("");
  const [accountNum, setAccountNum] = useState("");
  const [ballance, setBallance] = useState("");
  interface Receipt {
    _id: string;
    transactionDate: string;
    transactionDescription: string;
    transferAmount: {
      $numberDecimal: string;
    };
    approvalStatus: string;
    recipientName: string;
    recipientBank: string;
    recipientAccountNumber: string;
    currency: string;
    swiftCode: string;
  }

  const [receipts, setReceipts] = useState<Receipt[]>([]);

  const handleLocalPaymentClick = () => {
    navigate("/customer-payment-form");
  };

  const handleMainMenuClick = () => {
    navigate("/");
  };

  const handleTransactionClick = () => {
    navigate("/transactions");
  };

  useEffect(() => {
    if (!authChecked) {
      return; // Do nothing until auth check is done
    }

    // Fetch the user's name when the component loads
    async function fetchUsername() {
      const name = await getUserName();
      if (name) setUsername(name);
    }
    async function fetchUserAccountNum() {
      const AN = await getUserAccountNum();
      if (AN) setAccountNum(AN);
    }
    async function fetchUserReceipts() {
      const receipts = await getPayments();
      if (receipts) setReceipts(receipts);
    }

    async function fetchBalance() {
      const B = await getBalance();
      if (B) setBallance(B);
    }

    fetchUsername();
    fetchUserAccountNum();
    fetchBalance();
    fetchUserReceipts();
  }, [authChecked]);

  if (!authChecked) {
    return null; // Render nothing until auth check is done
  }

  const handleLogOutClick = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const handlePayAgain = async (receipt: Receipt) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please log in to make a payment');
        return;
      }

      // Send the encrypted account number directly without trying to re-encrypt
      const response = await fetch(
        "https://localhost:3000/api/transaction/transactFromReceipt",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`
          },
          body: JSON.stringify({
            recipientName: receipt.recipientName,
            recipientBank: receipt.recipientBank,
            recipientAccountNumber: receipt.recipientAccountNumber, // Send encrypted account number
            transferAmount: receipt.transferAmount.$numberDecimal,
            currency: receipt.currency,
            swiftCode: receipt.swiftCode,
            transactionDescription: receipt.transactionDescription,
            transactionDate: new Date().toISOString(),
          }),
        }
      );

      if (response.ok) {
        alert("Payment initiated successfully!");
        navigate("/transactions");
      } else {
        const errorData = await response.json();
        alert(`Payment failed: ${errorData.error}`);
      }
    } catch (error) {
      console.error("Error:", error);
      alert(`Payment failed: ${error instanceof Error ? error.message : String(error)}`);
    }
  };

  return (
    <div className="dashboard-container">
      {/* Side Navigation Bar */}
      <div className="side-nav">
        <button className="nav-button" onClick={handleMainMenuClick}>
          Main Menu
        </button>
        <button className="nav-button" onClick={handleTransactionClick}>
          Transactions
        </button>
        <button className="nav-button" onClick={handleLogOutClick}>
          Log Out
        </button>
      </div>

      {/* Main Dashboard Content */}
      <div className="main-content">
        <h1>Customer Dashboard</h1>

        {/* Greeting, Banking Details, and Payment Button Container */}
        <div className="greeting-banking-container">
          <div className="greeting-section">
            <h2>Hello, {username}</h2>
            <button
              className="make-payment-button"
              onClick={handleLocalPaymentClick}
            >
              Make Payment
            </button>
          </div>

          <div className="banking-details">
            <h2>Banking Details</h2>
            <div className="details-box">
              <p>Acc No: {accountNum}</p>
              <p>
                <strong>Available Balance</strong>: $ {ballance}
              </p>
            </div>
          </div>
        </div>

        {/* Payment Receipts Section */}
        <div className="payment-receipts">
          <h3>Payment Receipts</h3>
          <div className="details-box">
            {receipts.length > 0 ? (
              receipts.map((receipt, index) => (
                <div key={`${receipt._id}-${index}`} className="receipt-card">
                  <div className="receipt-header">
                    <h4>Payment Receipt</h4>
                    <span className="receipt-date">
                      {new Date(receipt.transactionDate).toLocaleString('en-US', {
                        year: 'numeric',
                        month: 'long',
                        day: 'numeric',
                        hour: '2-digit',
                        minute: '2-digit'
                      })}
                    </span>
                  </div>
                  <div className="receipt-body">
                    <div className="receipt-row">
                      <span className="receipt-label">Description:</span>
                      <span className="receipt-value">{receipt.transactionDescription}</span>
                    </div>
                    <div className="receipt-row">
                      <span className="receipt-label">Amount:</span>
                      <span className="receipt-value receipt-amount">
                        ${parseFloat(receipt.transferAmount.$numberDecimal).toFixed(2)}
                      </span>
                    </div>
                  </div>
                  <div className="receipt-footer">
                    <button 
                      className="pay-again-button"
                      onClick={() => handlePayAgain(receipt)}
                    >
                      Pay again
                    </button>
                  </div>
                </div>
              ))
            ) : (
              <p>No completed payments found.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default CustomerDashboard;

import React, { useState, useEffect, useRef } from "react";
import "../css/PaymentForm.css";
import SwiftCodeTextBox from "../components/SwiftCodeTextBox"; // Import the SwiftCodeTextBox component
import { isAuthenticated } from "../utils/auth";
import { useNavigate } from "react-router-dom";
import { getUserAccountNum } from "../services/dataRequestService";
import useFormValidationErrors from "../validation/useFormValidationErrors";
import {
  getAccountNumberErrors,
  getDescriptionErrors,
  getNameErrors,
  getTransferAmountErrors,
} from "../validation/validation";

function CustomerPaymentForm() {
  const [recipientName, setRecipientName] = useState("");
  const [recipientBank, setRecipientBank] = useState("");
  const [recipientAccountNumber, setRecipientAccountNumber] = useState("");
  const [transferAmount, setTransferAmount] = useState("");
  const [swiftCode, setSwiftCode] = useState("");
  const [currency, setCurrency] = useState("USD"); // Example currency
  const [description, setdescription] = useState(""); // Description of the transaction
  const [isSwiftCodeValid, setIsSwiftCodeValid] = useState<boolean>(false);
  const navigate = useNavigate();
  const alertShown = useRef(false); // Ref to track if the alert has been shown

  // custom hook for input validation
  const { errors, setFieldError, clearFieldError } = useFormValidationErrors([
    "recipientName",
    "recipientBank",
    "recipientAccountNumber",
    "transferAmount",
    "description",
  ]);

  useEffect(() => {
    if (!isAuthenticated() && !alertShown.current) {
      alert("You are not logged in. Please log in to continue.");
      alertShown.current = true; // Set the ref to true after showing the alert
      navigate("/login");
    }
  }, [navigate]);

  const handleBackClick = () => {
    navigate("/customer-dashboard");
  };
  const [accountNum, setAccountNum] = useState("");

  async function fetchUserAccountNum() {
    const AN = await getUserAccountNum();
    if (AN) setAccountNum(AN);
  }

  const handleIsValidChange = (valid: boolean) => {
    setIsSwiftCodeValid(valid);
  };

  const handlePayClick = async (e: any) => {
    fetchUserAccountNum();
    e.preventDefault(); // Prevent the default form submission

    // Input Validation
    clearFieldError("recipientName"); // Recipient Name
    const nameErrors = getNameErrors(recipientName);
    if (nameErrors.length > 0) {
      setFieldError("recipientName", nameErrors);
      return;
    }
    clearFieldError("recipientBank"); // Recipient Bank
    const recipientBankErrors = getNameErrors(recipientBank);
    if (recipientBankErrors.length > 0) {
      setFieldError("recipientBank", recipientBankErrors);
      return;
    }
    clearFieldError("recipientAccountNumber"); // Recipient Account Number
    const recipientAccountNumberErrors = getAccountNumberErrors(
      recipientAccountNumber
    );
    if (recipientAccountNumberErrors.length > 0) {
      setFieldError("recipientAccountNumber", recipientAccountNumberErrors);
      return;
    }
    clearFieldError("transferAmount"); // Transfer Amount
    const transferAmountErrors = getTransferAmountErrors(transferAmount);
    if (transferAmountErrors.length > 0) {
      setFieldError("transferAmount", transferAmountErrors);
      return;
    }
    clearFieldError("description"); // Description
    const descriptionErrors = getDescriptionErrors(description);
    if (descriptionErrors.length > 0) {
      setFieldError("description", descriptionErrors);
      return;
    }

    if (!isSwiftCodeValid)
     {
       return;
     }

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        alert('Please log in to make a payment');
        return;
      }



      const response = await fetch(
        "https://localhost:3000/api/transaction/transact",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}` // Add this line
          },
          body: JSON.stringify({
            recipientName,
            recipientBank,
            recipientAccountNumber,
            transferAmount,
            currency,
            swiftCode,
            transactionDescription: description,
            transactionDate: new Date().toISOString(),
          }),
        }
      );

      console.log("Server response:", response); // Log the raw response from the server

      if (response.ok) {
        const data = await response.json();
        console.log("Response data:", data); // Log the parsed response data
        alert("Payment Successful!");
        navigate("/customer-dashboard");
      } else {
        const errorData = await response.json();
        console.log("Error data:", errorData); // Log the error data
        alert(`Payment failed: ${errorData.error}`);
      }
    } catch (error: any) {
      console.error("Fetch error:", error); // Log the fetch error
      alert(`Payment failed: ${error.message}`);
    }
  };

  return (
    <div className="page-container">
      <div className="form-container">
        <h1 className="form-title">Payment Form</h1>
        <form className="payment-form" onSubmit={handlePayClick}>
          <div className="form-group">
            <label htmlFor="recipient-name">Recipient's Name:</label>
            <input
              className="input-field"
              type="text"
              id="recipient-name"
              value={recipientName}
              onChange={(e) => setRecipientName(e.target.value)}
              placeholder="Enter Recipient's Name"
            />
            <text className="global-error-text">{errors["recipientName"]}</text>
          </div>
          <div className="form-group">
            <label htmlFor="recipient-bank">Recipient's Bank:</label>
            <input
              className="input-field"
              type="text"
              id="recipient-bank"
              value={recipientBank}
              onChange={(e) => setRecipientBank(e.target.value)}
              placeholder="Enter Recipient's Bank"
            />
            <text className="global-error-text">{errors["recipientBank"]}</text>
          </div>
          <div className="form-group">
            <label htmlFor="recipient-account-no">
              Recipient's account no:
            </label>
            <input
              className="input-field"
              type="text"
              id="recipient-account-no"
              value={recipientAccountNumber}
              onChange={(e) => setRecipientAccountNumber(e.target.value)}
              placeholder="Enter Recipient's Account No"
            />
            <text className="global-error-text">
              {errors["recipientAccountNumber"]}
            </text>
          </div>
          <div className="form-group">
            <label htmlFor="amount-transfer">Amount to transfer:</label>
            <input
              className="input-field"
              type="text"
              id="amount-transfer"
              value={transferAmount}
              onChange={(e) => setTransferAmount(e.target.value)}
              placeholder="Enter Amount you want to pay"
            />
            <text className="global-error-text">
              {errors["transferAmount"]}
            </text>
          </div>
          <div className="form-group">
            <label htmlFor="description">Description:</label>
            <input
              className="input-field"
              type="text"
              id="description"
              placeholder="Enter payment description"
              value={description}
              onChange={(e: React.ChangeEvent<HTMLInputElement>) =>
                setdescription(e.target.value)
              }
            />
            <text className="global-error-text">{errors["description"]}</text>
          </div>
          <div className="form-group">
            <label htmlFor="currency">Currency:</label>
            <select
              className="input-field"
              id="currency"
              value={currency}
              onChange={(e) => setCurrency(e.target.value)}
            >
                <option value="ZAR">ZAR</option>
                <option value="USD">USD</option>
                <option value="EUR">EUR</option>
                <option value="GBP">GBP</option>
                <option value="JPY">JPY</option>
                <option value="AUD">AUD</option>
                <option value="CAD">CAD</option>
                <option value="CHF">CHF</option>
                <option value="CNY">CNY</option>
                <option value="INR">INR</option>
                <option value="BRL">BRL</option>
                <option value="MXN">MXN</option>
                <option value="RUB">RUB</option>
                <option value="KRW">KRW</option>
                <option value="SGD">SGD</option>
                <option value="HKD">HKD</option>
                <option value="NOK">NOK</option>
                <option value="SEK">SEK</option>
                <option value="NZD">NZD</option>
                <option value="TRY">TRY</option>

              {/* Add more currencies as needed */}
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="swift-code">Enter SWIFT Code:</label>
            <SwiftCodeTextBox value={swiftCode} onChange={(data) => {setSwiftCode(data)}} onIsValidChange={handleIsValidChange} />{" "}
            {/* Assuming SwiftCodeTextBox takes a value and onChange prop */}
          </div>
          <div className="form-buttons">
            <button type="submit" className="pay-now-button">
              PAY Now
