    return false;
  }
}

import crypto from 'crypto';

export const createLookupHash = (accountNumber: string): string => {
    const ENCRYPTION_KEY = process.env.REACT_APP_ENCRYPTION_KEY;

    if (!ENCRYPTION_KEY) {
        throw new Error('REACT_APP_ENCRYPTION_KEY environment variable is not set');
    }

    // Ensure accountNumber is a string and trim any whitespace
    const normalizedAccountNumber = String(accountNumber).trim();

    return crypto
        .createHash('sha256')
        .update(normalizedAccountNumber + ENCRYPTION_KEY)
        .digest('hex');
};

import { useState } from "react";

function useFormValidationErrors(fields: string[]) {
  // Initialize the errors object state dynamically based on the fields array
  const [errors, setErrors] = useState<{ [key: string]: string[] }>(
    fields.reduce((acc, field) => {
      acc[field] = [];
      return acc;
    }, {} as { [key: string]: string[] })
  );

  // Function to set (overwrite) error messages for a specific field
  const setFieldError = (fieldName: string, errorMessages: string[]) => {
    setErrors((prevErrors) => ({
      ...prevErrors,
      [fieldName]: errorMessages,
    }));
  };

  // Function to clear error messages for a specific field
  const clearFieldError = (fieldName: string) => {
    setErrors((prevErrors) => ({
      ...prevErrors,
      [fieldName]: [],
    }));
  };

  // Return the current errors and the functions to modify them
  return {
    errors,
    setFieldError,
    clearFieldError,
  };
}

