export function getConfirmPasswordErrors(password: string, confirmPassword: string): string[]
{
  var errors: string[] = [];
  if (confirmPassword === "")
  {
    errors.push('Confirm Password cannot be empty');
  }
  else if (validateSQLInjection(confirmPassword))
  {
